mapper.select:
SELECT COUNT(CAQ.COMPANY_ALGORITHM_QUESTION_ID) FROM COMPANY_ALGORITHM_QUESTION CAQ INNER JOIN MEMBER M ON CAQ.ALGORITHM_WRITER = M.EMAIL INNER JOIN ALGORITHM_CATEGORY_LIST ACL ON ACL.COMPANY_ALGORITHM_QUESTION_ID = CAQ.COMPANY_ALGORITHM_QUESTION_ID WHERE CAQ.DELETE_YN = 'N'
AND M.NICKNAME LIKE '%' || #{searchKeyword} || '%'
AND ACL.ALGORITHM_CATEGORY_ID IN
#{category}
AND CAQ.ALGORITHM_TIER_ID IN
#{tier}

mapper.insert:
INSERT INTO ALGORITHM_CATEGORY_LIST (ALGORITHM_CATEGORY_LIST_ID , COMPANY_ALGORITHM_QUESTION_ID , ALGORITHM_CATEGORY_ID) SELECT 'AC-' || TO_CHAR(SYSDATE, 'YYYYMMDD') || '-' || LPAD(SEQ_ALGORITHM_CATEGORY_LIST_PK.NEXTVAL, 6, '0') , Q_ID , C_ID FROM (
SELECT #{companyAlgorithmQuestionId} Q_ID , #{categoryId} C_ID FROM DUAL
)

mapper.update:
UPDATE COMPANY_ALGORITHM_QUESTION
   SET VIEW_CNT = VIEW_CNT + 1
 WHERE COMPANY_ALGORITHM_QUESTION_ID = #{_parameter}

    UPDATE COMPANY_ALGORITHM_QUESTION
	   SET MAIN_ALGORITHM_CATEGORY_ID = #{mainAlgorithmCategoryId}
	     , ALGORITHM_TIER_ID = #{algorithmTierId}
	     , ALGORITHM_TITLE = #{algorithmTitle}
	     , ALGORITHM_CONTENT = #{algorithmContent}
	     , ALGORITHM_SOLUTION = #{algorithmSolution}
	     , DEFAULT_CODE =#{defaultCode}
	 WHERE COMPANY_ALGORITHM_QUESTION_ID = #{companyAlgorithmQuestionId}

mapper.insert:
INSERT INTO ALGORITHM_CATEGORY_LIST (ALGORITHM_CATEGORY_LIST_ID , COMPANY_ALGORITHM_QUESTION_ID , ALGORITHM_CATEGORY_ID) SELECT 'AC-' || TO_CHAR(SYSDATE, 'YYYYMMDD') || '-' || LPAD(SEQ_ALGORITHM_CATEGORY_LIST_PK.NEXTVAL, 6, '0') , Q_ID , C_ID FROM (
SELECT #{companyAlgorithmQuestionId} Q_ID , #{categoryId} C_ID FROM DUAL
)

mapper.update:
    UPDATE COMPANY_ALGORITHM_QUESTION
       SET DELETE_YN = 'Y'
	 WHERE COMPANY_ALGORITHM_QUESTION_ID = #{companyAlgorithmQuestionId}
