mapper.update:
UPDATE COMPANY_RECRUIT_POST
   SET POST_TITLE = #{postTitle}
     , POST_CONTENT = #{postContent}
     , POST_DATE = SYSDATE
 WHERE COMPANY_RECRUIT_POST_ID = #{companyRecruitPostId}

